definition.script:
pipeline {
    agent any

    stages {
        stage('checkout') {
            steps {
                git branch: 'master', url: 'https://github.com/Fatalwgx/cv-web-app-fastapi-jinga2-htmx-postgresql/tree/master/automation-tests'
            }
        }
        stage('test execution') {
            steps {
                sh 'pytest ./tests/ --alluredir=allure-results --browser_version="chrome"'
            }
        }
        stage('reporting') {
            steps {
                allure includeProperties: false, jdk: '', results: [[path: 'allure-results']]
            }
        }
    }
}
